    <script>
        var $status = $('.pagingInfo');
        var $slickElement = $('.slideshow');

        $slickElement.on('init reInit afterChange', function (event, slick, currentSlide, nextSlide) {
        //currentSlide is undefined on init -- set it to 0 in this case (currentSlide is 0 based)
        var i = (currentSlide ? currentSlide : 0) + 1;
        $status.text(i + '/' + slick.slideCount);
        });
        $('.center').slick({
            centerMode: true,
            centerPadding: '60px',
            slidesToShow: 1,
            adaptiveHeight: true,
            prevArrow:"<button type='button' class='slick-prev pull-left align-custom'><img src='assets/images/fa-replace/previous.svg' aria-hidden='true' /></button>",
            nextArrow:"<button type='button' class='slick-next pull-right align-custom'><img src='assets/images/fa-replace/next.svg' aria-hidden='true' /></i></button>",
            responsive: [{
                breakpoint: 768,
                settings: {
                    arrows: true,
                    centerMode: true,
                    centerPadding: '40px',
                    slidesToShow: 1
                }
            }, {
                breakpoint: 480,
                settings: {
                    arrows: true,
                    centerMode: true,
                    centerPadding: '40px',
                    slidesToShow: 1
                }
            }]
        });
        $('.team').slick({
        dots: false,
        infinite: false,
        speed: 400,
        slidesToShow: 3,
        slidesToScroll: 1,
        prevArrow:"<button type='button' class='slick-prev pull-left align-custom'><img src='assets/images/fa-replace/previous.svg' aria-hidden='true' /></button>",
        nextArrow:"<button type='button' class='slick-next pull-right align-custom'><img src='assets/images/fa-replace/next.svg' aria-hidden='true' /></button>",
        responsive: [
            {
            breakpoint: 1024,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    infinite: false,
                    dots: false
                }
            },
            {
            breakpoint: 640,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1
                }
            },
            {
            breakpoint: 480,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1
                }
            } 
        ]
        });
        
        $("ul#agency-wp-list li").click(function() {
            $(this).addClass('active');
            let allPrevSiblings = $(this).prevAll();
            let allNextSiblings = $(this).nextAll();
            if (allPrevSiblings.length) {
                allPrevSiblings.each(function(i, t){
                    $(t).addClass('active');
                })
            }
            allNextSiblings.each(function(i, t){
                $(t).removeClass('active');
            });
        });

        // Example starter JavaScript for disabling form submissions if there are invalid fields
        (function() {
            'use strict'

            // Fetch all the forms we want to apply custom Bootstrap validation styles to
            var forms = document.querySelectorAll('.needs-validation')

            // Loop over them and prevent submission
            Array.prototype.slice.call(forms)
                .forEach(function(form) {
                    form.addEventListener('submit', function(event) {
                        if (!form.checkValidity()) {
                            event.preventDefault()
                            event.stopPropagation()
                        }

                        form.classList.add('was-validated')
                    }, false)
                })
        })()
        // Get all sections that have an ID defined
        const sections = document.querySelectorAll("section[id]");

        // Add an event listener listening for scroll
        window.addEventListener("scroll", navHighlighter);

        function navHighlighter() {

            // Get current scroll position
            let scrollY = window.pageYOffset;

            // Now we loop through sections to get height, top and ID values for each
            sections.forEach(current => {
                const sectionHeight = current.offsetHeight;
                const sectionTop = current.offsetTop - 96;
                sectionId = current.getAttribute("id");

                if (
                    scrollY > sectionTop &&
                    scrollY <= sectionTop + sectionHeight
                ) {
                    document.querySelector(".navbar-nav a[href*=" + sectionId + "]").classList.add("current");
                } else {
                    document.querySelector(".navbar-nav a[href*=" + sectionId + "]").classList.remove("current");
                }
            });
        }
    </script>
